const std = @import("std");
const debug = std.debug;
const fmt = std.fmt;
const mem = std.mem;

const ascii_case_offset: i32 = 'a' - 'A';
const destroyed_signifier: u8 = '*';

pub fn main() !void {
    var allocator = &std.heap.DirectAllocator.init().allocator;
    debug.warn("Warning. This will take a few minutes :(...\n");
    var result = try improved_length(allocator, input_polymer);
    debug.assert(result == 6942);
    debug.warn("05-2 {}\n", result);
}

fn improved_length(allocator: *mem.Allocator, polymer: []const u8) !usize {
    const unit_types = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

    var shortest_length: usize = polymer.len;
    var most_effective_unit_to_remove: u8 = '*';

    for (unit_types) |unit_type| {
        var p = try allocator.alloc(u8, polymer.len);
        defer allocator.free(p);

        mem.copy(u8, p, polymer);
        for (p) |*unit, i| {
            if ((unit.* == unit_type or unit.* == unit_type + 32)) {
                    unit.* = destroyed_signifier;
            }
        }

        p = try shrink(allocator, p);
        var result = try react(allocator, p);

        //debug.warn("{c}: {}\n", unit_type, result.len);

        if (result.len < shortest_length) {
            most_effective_unit_to_remove = unit_type;
            shortest_length = result.len;
        }
    }

    //debug.warn("{c}\n", most_effective_unit_to_remove);
    return shortest_length;
}

test "improved length" {
    var allocator = &std.heap.DirectAllocator.init().allocator;
    debug.assert(4 == try improved_length(allocator, "dabAcCaCBAcCcaDA"));
}

fn react(allocator: *mem.Allocator, polymer: []const u8) ![]const u8 {
    var p = try allocator.alloc(u8, polymer.len);
    mem.copy(u8, p, polymer);

    var last_len: usize = p.len + 1;
    while (p.len < last_len) {
        //debug.warn("{}\n", p);
        last_len = p.len;
        for (p) |*unit, i| {
            if (i + 1 < p.len) {
                var next_unit = p[i + 1];
                if (@intCast(i32, unit.*) - @intCast(i32, next_unit) == ascii_case_offset or
                    @intCast(i32, unit.*) - @intCast(i32, next_unit) == -ascii_case_offset) {
                    unit.* = destroyed_signifier;
                    p[i + 1] = destroyed_signifier;
                    p = try shrink(allocator, p);
                    break;
                }
            }
        }
        if (p.len == 0) {
            return p;
        }
    }
    return p;
}

fn shrink(allocator: *mem.Allocator, reacted_polymer: []const u8) ![]u8 {
    defer allocator.free(reacted_polymer);

    //debug.warn("{}\n", reacted_polymer);
    var unit_count: usize = 0;
    for (reacted_polymer) |unit| {
        if (unit != destroyed_signifier) {
            unit_count += 1;
        }
    }

    if (unit_count == 0) {
        return "";
    }

    var product = try allocator.alloc(u8, unit_count);
    var product_i: usize = 0;
    for (reacted_polymer) |unit| {
        if (unit != destroyed_signifier) {
            debug.assert(product_i < product.len);
            //debug.warn("{c}: {}\n", unit, product_i);
            product[product_i] = unit;
            product_i += 1;
        }
    }
    return product;
}

test "react" {
    var allocator = &std.heap.DirectAllocator.init().allocator;
    debug.assert(mem.eql(u8, "", try react(allocator, "aA")));
    debug.assert(mem.eql(u8, "", try react(allocator, "abBA")));
    debug.assert(mem.eql(u8, "abAB", try react(allocator, "abAB")));
    debug.assert(mem.eql(u8, "aabAAB", try react(allocator, "aabAAB")));
    debug.assert(mem.eql(u8, "dabCBAcaDA", try react(allocator, "dabAcCaCBAcCcaDA")));
    debug.assert(mem.eql(u8, "dabCBAcaD", try react(allocator, "dabAcCaCBAcCcaD")));
}

const input_polymer = "";
